TWComponent.script:
var taskorInstanceID;

if (isNaN(Number(tw.local.data))) {
taskorInstanceID = 99999;
}else {
taskorInstanceID = tw.local.data;
}

tw.local.parameters[0] = new tw.object.toolkit.TWSYS.SQLParameter();
tw.local.parameters[0].type =  'VARCHAR';
tw.local.parameters[0].mode =  'IN';
tw.local.parameters[0].value = taskorInstanceID;

