inlineScript.scriptBlock:
var _this = this;
var userlimit = 1000;

function getUsersList(groupname) {
var xhrArgs = function(config) { return {
url: "/rest/bpm/wle/v1/users?includeTaskExperts=true&sort=true&includeInternalMemberships=true&refreshUser=false&parts=all", content: {
accept: "application/json"
},
load: displayUsers,
handleAs: "json"} };

try{

deferred = dojo.xhrGet(xhrArgs ( { inputs: {}}));

} catch (error) {
alert(error);
}

function displayUsers(json_results) {
try{
var userlist = _this.context.options.userList.get("value");
for (var a=0; a < userlist.listlength; a++){
userlist.pop();
}

for (var i=0; i < json_results.data.users.length && i < userlimit; i++){
var exists = false;
for (var j=0; j < userlist.items.length; j++){
if (json_results.data.users[i].userName.length > 73 || userlist.items[j].name == json_results.data.users[i].userName){
exists = true;
break;
}
}
if (!exists){
var userline = userlist.createListElement();
userline.name = json_results.data.users[i].userName;
userline.value = json_results.data.users[i].userID;
userlist.add(userline);
}
}
_this.context.options.userList.set("value", userlist);
} catch (error) {
alert(error);
}

}

}
