inlineScript.scriptBlock:
var _this = this;
var data1;
var instanceList;
var ab = 0;
var cd = 0;
var large = false;
var last = false;
var items;
var radio1;
this.manageinstance = function (control, instances) {
	var sdt = this.ui.get("instanceDetails1");
	data1 = this.ui.get("../Data1");
	instanceList = this.ui.get("instanceDetails1");
	items = sdt.getSelectedRecords();
	radio1 = this.ui.get("Radio_Button_Group1");

	radio1.setValid(radio1.getData() && radio1.getData().replace(" ", "")  != "", "Please select Instance Action to Perform"); 
if ( !radio1.getData() || radio1.getData().replace(" ", "")  == "") {
return radio1.isValid();
}

	try {
		if (items.length == 0) {
			data1.setValue(" Error: You have not selected any instances Please select instances to continue " + new Date());
		}
		if (items.length > 500) {
			data1.setValue(" Info: " + iaction + " of " + icount + " instance(s)  are greater than 500 They will be actioned in batches of 500" + new Date());
			large = true;
		}
		for (ab = 0; ab < items.length; ab = ab + 500) {
			last = false;
			var instances = "";
			var icount = 0;
			for (var i = ab; i < ab + 500 && i < items.length; i++) {
				instances += items[i].instanceID + ",";
				icount = i + 1;
				if (i == items.length - 1) {
					last = true;
				}
			}
			instances += "99999999";
			/*
			suspend
			Suspend the process instances.
			resume
			Resume the process instances.
			terminate
			Terminate the process instances.
			delete
			Delete the process instances.
			retry
			Retry the failed process instances.
			 */
			var iaction = this.ui.get("Radio_Button_Group1").getData();
			var xhrArgs = function (config) {
				return {
					url: "/rest/bpm/wle/v1/process/bulk?instanceIds=" + instances + "&action=" + iaction,
					content: {
						accept: "application/json"
					},
					load: displayImage, 
					handle: displayImage, 
					handleAs: "json"
				}
			}

			deferred = setTimeout(dojo.xhrPut(xhrArgs({
							inputs: {}
						})), ab * 10);

			function displayImage(json_results) {
				if (json_results && json_results.status == "200") {
					if (last) {
						data1.setValue(" Success: " + iaction + " of " + items.length + " instance(s) " + new Date());
					} else {
						data1.setValue(" Info: " + iaction + " of " + icount + " instance(s) So far processed " + ab + new Date());
					}
					instanceList.refresh();
					instanceList.setAllRecordsSelected(false);
				} else {
						if (json_results.responseText){
							var r = jQuery.parseJSON(json_results.responseText);
							data1.setValue(" Error: " + r.Data.errorMessage + new Date());
						}else {
							data1.setValue(" Error:  Unknowon Error" +  new Date());
						}
				}
			}		
		}
	} catch (error) {
		data1.setValue(" Error: " + iaction + " of " + icount + " instance(s) " + error);
	}

}
