var url = "/rest/bpm/wle/v1/task/" + taskID+ "?action=setData&params=" + message1;

var json = message1;
json = "";
var xhr = new XMLHttpRequest();
xhr.open("PUT", url, true);
xhr.setRequestHeader('Content-type','application/json; charset=utf-8');
xhr.onload = function () {
//	var users = JSON.parse(xhr.responseText);
	if (xhr.readyState == 4 && xhr.status == "200") {
		data1.setValue(" Success: Data for Task ID " + taskID+ " Updated Successfully" + new Date());
		modal.setVisible(false);
	} else {
					modal.setVisible(false);
						if (xhr.responseText){
							var r = jQuery.parseJSON(xhr.responseText);
							data1.setValue(" Error: " + r.Data.errorMessage + new Date());
						}else {
							data1.setValue(" Error:  Unknowon Error" +  new Date());

						}

	}
}
xhr.send(json);

