var _this = this;
var grouplimit = 500;

function getGroupList(username) {
var xhrArgs = function(config) { return {
url: "/rest/bpm/wle/v1/groups?includeDeleted=false&parts=all", content: {
accept: "application/json"
},
load: displayGroups,
handleAs: "json"} };

try{

deferred = dojo.xhrGet(xhrArgs ( { inputs: {}}));

} catch (error) {
alert(error);
}

function displayGroups(json_results) {
try{
var grouplist = _this.context.options.groupList.get("value");

 for (var a=0; a < grouplist.listlength; a++){
grouplist.pop();
}


for (var i=0; i < json_results.data.groups.length && i < grouplimit; i++){
var exists = false;
for (var j=0; j < grouplist.items.length; j++){
if (json_results.data.groups[i].groupName.length > 73 || grouplist.items[j].name == json_results.data.groups[i].displayName){
exists = true;
break;
}
}
if (!exists){
var groupline = grouplist.createListElement();
groupline.name = json_results.data.groups[i].displayName;
groupline.value = json_results.data.groups[i].groupID;
grouplist.add(groupline);
}
}
_this.context.options.groupList.set("value", grouplist);
} catch (error) {
alert(error);
}

}

}
