coachView.loadJsFunction:
var mainele = this.context.element;
_this = this;
//var sparkabs = mainele.querySelectorAll('[role="tab"]');

var tiles = this.context.element.querySelectorAll('.tile_count')[0];
if (_this.context.element.querySelectorAll('h3[id="SS1"]')[0].innerHTML == 'System Summary') {
	tiles.style.display = 'block';
} else if (this.context.options.hideOverviewBar) {
	tiles.style.display = 'none';
}

var items = this.context.element.querySelectorAll('a[onclick*="return"]');
for (i = 0; i < items.length; i++) {
	//alert(items[i]. id);
	items[i].onclick = function () {
		_this.context.options.selectTabNowIndex.set("value", this.id);
		_this.context.element.querySelectorAll('h3[id="SS1"]')[0].innerHTML = this.innerHTML;
		var tiles = _this.context.element.querySelectorAll('.tile_count')[0];
		if (this.innerHTML != "System Summary") {
			tiles.style.display = 'none';
		} else {
			tiles.style.display = 'block';
		}
		return false;
	}
}
var totusers = _this.context.element.querySelectorAll('.tile_stats_count > .count')[0];
var activedev = _this.context.element.querySelectorAll('.tile_stats_count > .count')[1];
var activepordev = _this.context.element.querySelectorAll('.tile_stats_count > .count')[2];
var activeinstance = _this.context.element.querySelectorAll('.tile_stats_count > .count')[3];
var activetasks = _this.context.element.querySelectorAll('.tile_stats_count > .count')[4];
var activeactiiv = _this.context.element.querySelectorAll('.tile_stats_count > .count')[5];

if (this.context.options.TDSHStatistics) {
	var stats = this.context.options.TDSHStatistics.get("value");

	totusers.innerHTML = stats.items[0].TOTALUSERS;
	activedev.innerHTML = stats.items[0].ACTIVEPCUSERS;
	activepordev.innerHTML = stats.items[0].ACTIVEPORTALUSERS;
	activeinstance.innerHTML = stats.items[0].ACTIVEBPDS;
	activetasks.innerHTML = stats.items[0].ACTIVETASKS;
	activeactiiv.innerHTML = stats.items[0].ACTIVEACTIVITIES;
}

generatePDF = function (pdfdivid) {
	var pdf = new jsPDF('p', 'pt', 'letter');
	var canvas = pdf.canvas;
	canvas.height = 72 * 11;
	canvas.width = 72 * 8.5; ;

	var pdfarea = _this.context.element.querySelector("#" + pdfdivid);

	// can also be document.body
	var html = pdfarea.innerHTML;

	html2pdf(html, pdf, function (pdf) {
		pdf.output('dataurlnewwindow');
	});
}

