inlineScript.scriptBlock:
var _this = this;
var items, items1, items2;
var data1;
var sdt;
var sdt1;
var searchlimit = 100;
this.addTokens = function () {
	sdt = this.ui.get("SelectedTokens1");
	sdt1 = this.ui.get("SelectedTokens2");

	items1 = sdt.getRecords(true);
	items2 = sdt1.getData();

	data1 = this.ui.get("../Data1");
	if (items1.length == 0) {
		data1.setValue(" Info: No Tokens found for the search criteria, Please try changing the criteria. " + new Date());
	} else if (items1.length >= searchlimit) {
		data1.setValue(" Info: More than " + searchlimit +" rows returned, only the first " + searchlimit + " are available. " + new Date());
	}
	var instances = '';
	for (var i = 0; i < items1.length && i < searchlimit; i++) {
		instances += items1[i].instanceID + ",";
	}
	instances += '999999';

	var xhrArgs = function (config) {
		return {
			url: "/rest/bpm/wle/v1/process?action=getdetails&instanceIds=" + instances + "&parts=all",
			content: {
				accept: "application/json"
			},
			load: displayImage,
			handle: displayImage, 
			handleAs: "json"
		}
	}
	deferred = setTimeout(dojo.xhrGet(xhrArgs({
					inputs: {}
				})), 0);

	function displayImage(json_results) {
		if (json_results && json_results.status == "200") {
			//alert(json_results.data);
			for (var j = 0; j < json_results.data.processDetails.length; j++) {
				for (var l = 0; l < json_results.data.processDetails[j].diagram.step.length; l++) {
					var found = false;
					for (m = 0; m < items1.length; m++) {
						if (items1[m].TaskID + "" == json_results.data.processDetails[j].diagram.step[l].taskID) {
							found = true;
							if (json_results.data.processDetails[j].diagram.step[l].tokenID.length == 1) {

								items1[m].currentBPDID = json_results.data.processDetails[j].diagram.step[l].ID;
								items1[m].tokenID = json_results.data.processDetails[j].diagram.step[l].tokenID[0];
								items1[m].processID = json_results.data.processDetails[j].processTemplateID;
								items1[m].snapshotID = json_results.data.processDetails[j].snapshotID;
							} else {
								for (var n = 1; n < json_results.data.processDetails[j].diagram.step[l].tokenID.length; n++) {
									var newitem = items1[m];
									newitem.currentBPDID = json_results.data.processDetails[j].diagram.step[l].ID;
									newitem.tokenID = json_results.data.processDetails[j].diagram.step[l].tokenID[n];
									newitem.processID = json_results.data.processDetails[j].processTemplateID;
									newitem.snapshotID = json_results.data.processDetails[j].snapshotID;
									items1.add(newitem);
								}
							}
						} else {
							/*				if (json_results.data.processDetails[j].diagram.step[l].tokenID && json_results.data.processDetails[j].diagram.step[l].tokenID.length > 0) {
							for(var n=0; n < json_results.data.processDetails[j].diagram.step[l].tokenID.length; n++) {
							if (json_results.data.processDetails[j].piid == items1[m].instanceID) {
							items1[m].currentBPDID = json_results.data.processDetails[j].diagram.step[l].ID;
							items1[m].tokenID = json_results.data.processDetails[j].diagram.step[l].tokenID[n];
							//items1[m].TaskID = json_results.data.processDetails[j].diagram.step[l].taskID;
							//items1[m].TaskSubject = "";
							//items1[m].ActivityName = json_results.data.processDetails[j].diagram.step[l].name;
							}
							}
							}*/
						}
					}
					if (!found) {
						//var row = items1.createListElement();
						//row.tokednID
					}

				}

				for (var x = 0; x < json_results.data.processDetails[j].executionTree.root.children.length; x++) {
					if (json_results.data.processDetails[j].executionTree.root.children[x].children) {
						for (var y = 0; y < json_results.data.processDetails[j].executionTree.root.children[x].children.length; y++) {
							if (json_results.data.processDetails[j].executionTree.root.children[x].children[y].children) {
								for (var z = 0; z < json_results.data.processDetails[j].executionTree.root.children[x].children[y].children.length; z++) {
									for (m = 0; m < items1.length; m++) {
										if (json_results.data.processDetails[j].executionTree.root.children[x].children[y].children[z].children && json_results.data.processDetails[j].executionTree.root.children[x].children[y].children[z].children[0].createdTaskIDs && items1[m].TaskID + "" == json_results.data.processDetails[j].executionTree.root.children[x].children[y].children[z].children[0].createdTaskIDs[0]) {
											items1[m].currentBPDID = json_results.data.processDetails[j].executionTree.root.children[x].children[y].children[z].children[0].flowObjectId;
											items1[m].tokenID = json_results.data.processDetails[j].executionTree.root.children[x].children[y].children[z].children[0].tokenId;
											items1[m].processID = json_results.data.processDetails[j].executionTree.root.children[x].children[y].children[z].processId;
											items1[m].BPDName = json_results.data.processDetails[j].executionTree.root.children[x].children[y].children[z].name
												items1[m].snapshotID = json_results.data.processDetails[j].snapshotID;
										}
									}
								}
							}
						}
					}
				}

			}
			sdt1.clear();
			for (var p = 0; p < items1.length && p < searchlimit; p++) {
				if (items1[p].tokenID && items1[p].tokenID != "") {
					items2.add(items1[p]);
				}
			}
			if (items2.length == 0){
			//data1.setValue(" Info: No Tokens found for the search criteria, Please try changing the criteria." + r.Data.errorMessage + new Date());
			sdt1.clear();
			} else {
			sdt1.appendElements(items2);
			}
		} else {
						if (json_results.responseText){
							var r = jQuery.parseJSON(json_results.responseText);
							data1.setValue(" Error: " + r.Data.errorMessage + new Date());
						} else {
							//data1.setValue(" Error:  Unknowon Error" +  new Date());
							sdt1.clear();

						}
				}
	}
}

this.moveToken = function () {
	sdt = this.ui.get("SelectedTokens1");
	sdt1 = this.ui.get("SelectedTokens2");
	items = sdt1.getSelectedRecords();
	var target = this.ui.get("Process_Steps_Single_Select1");
	var targetid = target.getData().name;
	var targetName = target.getData().value;
	
	radio1 = this.ui.get("Process_Steps_Single_Select1/ProcessStepsSingle_Select1");

	radio1.setValid(radio1.getData().value && radio1.getData().value.replace(" ", "")  != "", "Please Choose Token(s) to move and then select Target Step to Move token to"); 
	if ( !radio1.getData().value || radio1.getData().value.replace(" ", "")  == "") {
		return radio1.isValid();
	}
		//alert(targetid);
		data1 = this.ui.get("../Data1");
		if (!items || items.length == 0) {
			data1.setValue("Error: You have not selected any Token Please select Token to continue" + new Date());
		}

	for (var i = 0; i < items.length; i++) {

		if (!targetid) {
			data1.setValue("Error: You have not selected any Target Please select Target Activity to continue" + new Date());
			target.setSelectedItem(null);
		}

		var xhrArgs = function (config) {
			return {
				url: "/rest/bpm/wle/v1/process/" + items[i].instanceID + "?action=moveToken&tokenId=" + items[i].tokenID + "&target=" + targetid + "&resume=true&parts=none",
				content: {
					accept: "application/json"
				},
				load: displayImage,
				handle: displayImage,
				handleAs: "json"
			}
		}

		deferred = setTimeout(dojo.xhrPost(xhrArgs({
						inputs: {}
					})), i * 1000);
	}

	function displayImage(json_results) {
		if (json_results && json_results.status == "200") {
			data1.setValue(" Success: Moved " + items.length + " Tokens to  " + targetName + " " + new Date());
			sdt.refresh();
			sdt1.setAllRecordsSelected(false);
		} else {
						if (json_results.responseText){
							var r = jQuery.parseJSON(json_results.responseText);
							data1.setValue(" Error: " + r.Data.errorMessage + new Date());
							data1.setValue(" Error: Not Moved Token  to  " + targetName + " " + json_results.Data.errorMessage);
						} else {
							data1.setValue(" Error:  Unknowon Error" +  new Date());
							data1.setValue(" Error: Not Moved Token  to  " + targetName + " " + json_results.Data.errorMessage);


						}
				}
		
	}

}

this.deleteToken = function () {
	sdt = this.ui.get("SelectedTokens1");
	sdt1 = this.ui.get("SelectedTokens2");
	items = sdt1.getSelectedRecords();
	var target = this.ui.get("Process_Steps_Single_Select1");
	var targetid = target.getData().name;
	var targetName = target.getData().value;

		data1 = this.ui.get("../Data1");
		if ( !items || items.length == 0) {
			data1.setValue("Error: You have not selected any Token Please select Token to continue" + new Date());
		}

	for (var i = 0; i < items.length; i++) {

		//alert(targetid);

		var xhrArgs = function (config) {
			return {
				url: "/rest/bpm/wle/v1/process/" + items[i].instanceID + "?action=deleteToken&tokenId=" + items[i].tokenID + "&resume=true&parts=none",
				content: {
					accept: "application/json"
				},
				load: displayImage,
				handle: displayImage,
				handleAs: "json"
			}
		}

		deferred = setTimeout(dojo.xhrPost(xhrArgs({
						inputs: {}
					})), i * 1000);
	}

	function displayImage(json_results) {
		if (json_results && json_results.status == "200") {
			data1.setValue(" Success: Deleted " + items.length + " Tokens to  " + targetName + " " + new Date());
			sdt.refresh();
			sdt1.setAllRecordsSelected(false);
		}  else {
						if (json_results.responseText){
							var r = jQuery.parseJSON(json_results.responseText);
							data1.setValue(" Error: " + r.Data.errorMessage + new Date());
							data1.setValue(" Error: Unable to delete " + items.length + "Token(s) " + json_results.Data.errorMessage);

						}else {
							data1.setValue(" Error:  Unknowon Error" +  new Date());
							data1.setValue(" Error: Unable to delete " + items.length + "Token(s) " + json_results.Data.errorMessage);

						}
				}
		
	}

}
