inlineScript.scriptBlock:
  var _this = this;
var data1;
var sdt;
var grid;
var modal;
var message = "";
var instanceID = "";
this.executeJavascript = function () {
	sdt = this.ui.get("JavascriptToExecute_Text_Area1");
	modal = this.ui.get("ExecuteJavascript_Modal_Section1");
	grid = this.ui.get("InstanceID_Data1");
      instanceID = grid.getData();
      message = sdt.getData();
      message = encodeURIComponent(message);
	data1 = this.ui.get("../Data1");

	var xhrArgs = function (config) {
		return {
			url: "/rest/bpm/wle/v1/process/" + instanceID + "?action=js&script="+ message + "&parts=none"   ,
			content: {
				accept: "application/json"
			},
			load: displayImage,
			handle: displayImage,
			handleAs: "json"
		}
	}

	deferred = setTimeout(dojo.xhrPut(xhrArgs({
					inputs: {}
				})), 0);

	function displayImage(json_results) {
		if (json_results && json_results.status == "200") {
		data1.setValue(" Success: Message Sent Successfully" + new Date());
		//grid.refresh();
		modal.setVisible(false);

		} else {
		modal.setVisible(false);
			if (json_results.responseText){
				var r = jQuery.parseJSON(json_results.responseText);
				data1.setValue(" Error: " + r.Data.errorMessage + new Date());
			}else {
				data1.setValue(" Error:  Unknowon Error" +  new Date());
			}
		}
}
}
