inlineScript.scriptBlock:
this.exposeNote = function(layoutView){

	var subViews = false;
	for (x in layoutView.context.subview){ 
		 subViews = true;
	}
	
	var message;
	var noteView;
	var panel;
	if(layoutView.context.viewid === "i"){
		noteView = this.ui.get("InputNote");
		panel = this.ui.get("I_panel");
	}  else if(layoutView.context.viewid === "o"){ 
		noteView = this.ui.get("OutputNote");
		panel = this.ui.get("O_panel");
	}
	
	if(subViews === false){
		panel.collapse();
	} else{
		noteView.context.options._metadata.visibility.set("value","NONE");
	}

}
