inlineScript.scriptBlock:
 var _this = this;
var data1;
var sdt;
var sdt5;
var grid;
var modal;
var message = "";
var message1 = "";
var myobj ;
var taskID = "";
var key1="";
var users;

this.refreshUsers= function () {
//	sdt = this.ui.get("TaskJSONData_Text_Area1");
	grid = this.ui.get("UserServiceDataTable2");
//	taskID= this.ui.get("SelectedTaskID_Data1").getData();
//	modal = this.ui.get("UpdateTaskData_Modal_Section2");

      users = grid.getSelectedRecords();;
	data1 = this.ui.get("../Data1");
	
	if (!users || users.length == 0){
	
	data1.setValue(" Error:  You have not selected any users Please select users to refresh!" +  new Date());

	}
		for (var i = 0; i < users.length; i++) {
		

	var xhrArgs = function (config) {
		return {
			url:  "/rest/bpm/wle/v1/user?userID=" + users[i].userID + "&includeInternalMemberships=true&refreshUser=true&includeEditableUserPreferences=false&parts=none",
			content: {
				accept: "application/json"
			},
			load: displayImage, 
			handle: displayImage, 
			handleAs: "json"
		}
	}

	deferred = setTimeout(dojo.xhrGet(xhrArgs({
					inputs: {}
				})), 0);
		}

	function displayImage(json_results) {
		if (json_results && json_results.status == "200") {
		data1.setValue(" Success: User " + json_results.data.userID + " :  "+ json_results.data.userName + " Refreshed  "+ new Date());
		} else {
				//modal.setVisible(false);
						if (json_results.responseText){
						try{
							var r = jQuery.parseJSON(json_results.responseText);
							data1.setValue(" Error: " + r.Data.errorMessage + new Date());
							} catch(e){
							data1.setValue(" Error: " + json_results.responseText + new Date());
							}
						}else {
							data1.setValue(" Error:  Unknowon Error" +  new Date());

						}
				}
}

}




this.refreshAllUsers= function () {
//	sdt = this.ui.get("TaskJSONData_Text_Area1");
	grid = this.ui.get("UserServiceDataTable2");
//	taskID= this.ui.get("SelectedTaskID_Data1").getData();
//	modal = this.ui.get("UpdateTaskData_Modal_Section2");

      users = grid.getSelectedRecords();;
	data1 = this.ui.get("../Data1");
	var xhrArgs = function (config) {
		return {
			url:  "/rest/bpm/wle/v1/users?includeTaskExperts=true&sort=true&includeInternalMemberships=true&refreshUser=true&parts=none",
			content: {
				accept: "application/json"
			},
			load: displayImage, 
			handle: displayImage, 
			handleAs: "json"
		}
	}

	deferred = setTimeout(dojo.xhrGet(xhrArgs({
					inputs: {}
				})), 0);

	function displayImage(json_results) {
		if (json_results && json_results.status == "200") {
		data1.setValue(" Success: Refreshed " + json_results.data.users.length + " Users "+ new Date());
		} else {
				//modal.setVisible(false);
						if (json_results.responseText){
						try{
							var r = jQuery.parseJSON(json_results.responseText);
							data1.setValue(" Error: " + r.Data.errorMessage + new Date());
							} catch(e){
							data1.setValue(" Error: " + json_results.responseText + new Date());
							}
						}else {
							data1.setValue(" Error:  Unknowon Error" +  new Date());

						}
				}
}

}
